(function (window, MALTween, MALAd, undefined) {
	var timing = MALTween.timing;
	var $ = function ($element) { return window.document.getElementById($element); };
	
	MALAd.imgPath = 'http://speed.pointroll.com/PointRoll/Media/Panels/Apple/738231/';
	MALAd.images = [
		'1x1.gif',
		'bg.jpg',
		'dl_cta.png',
		'dl_sprite.png',
		'shard_a_8.png',
		'shard_a_24.png',
		'shard_b_8.png',
		'shard_b_24.png',
		'shard_c_8.png',
		'shard_c_24.png',
		'shard_d_8.png',
		'shard_d_24.png',
		'shard_e2.png',
		'shard_f2_8.png',
		'shard_f2_24.png',
		'shard_g_8.png',
		'shard_g_24.png',
		'title_shadow copy.png',
		'title.png'
	];
	
		MALAd.setup = function () {
		MALTween.init();
		
		// standard tweens
		MALTween.add('MALAdCTAOver', {});
		MALTween.add('MALAdClicker', {})
				.addEvent('click', function (e) {
					MALAd.clickThrough();
				})
				.addEvent('mouseover', function (e) {
					if (MALAd.mouseOver == true) {
						MALTween.MALAdCTAOver.update({ time: 0.3003,opacity: 1, transition: timing('linear') });
					}
				})
				.addEvent('mouseout', function (e) {
					if (MALAd.mouseOver == true) {
						MALTween.MALAdCTAOver.update({ time: 0.3003, opacity: 0, transition: timing('linear') });
					}
				});

		var shards = [
			['MALAdShard11', 0, 6.1728, -30, -76, 1.1678, -74, -188, '0.76, 0.07, 0.86, 1'],
			['MALAdShard10', 3.8371, 2.4607, -59, 50, 0.9175, -71, 59, '0.59, 0.07, 0.88, 1'],
			['MALAdShard9', 0, 6.0894, 58, 64, 0.9592, 188, 214, 'linear'],
			['MALAdShard8', 3.8371, 2.5025, -62, 30, 0.8758, -265, 156, '0.79, 0.07, 0.86, 1'],
			['MALAdShard7', 0, 4.7964, 66, 36, 'linear'],
			['MALAdShard6', 3.8371, 2.5859, 13, 131, 0.9175, 16, 169, '0.88, 0.2, 0.86, 1'],
			['MALAdShard5', 0, 4.7964, 102, -37, 'linear'],
			['MALAdShard4', 3.8371, 2.5025, -62, 14, 0.9175, -344, 82, '0.74, 0.02, 0.9, 1'],
			['MALAdShard3', 0, 4.7547, -33, -98, 'linear'],
			['MALAdShard2', 0, 6.1728, 79, 37, 1.0427, 342, 175, '0.7, 0, 0.9, 1'],
			['MALAdShard1', 0, 6.1728, -121, -34, 1.1678, -330, -95, '0.64, 0, 0.95, 1']
		];

		for (var i = 0, l = shards.length; i < l; i++) {
			var div = shards[i][0];
			var delay = shards[i][1];			
			var time = shards[i][2];
			var x = shards[i][3];
			var y = shards[i][4];

			MALTween.add(div, {delay: delay, time: time, x: x, y: y, transition: timing('linear')});
			
			if (shards[i].length == 9) {
				MALTween[div].addEvent('complete', function () {
							var timeupdate1 = this.shard[5];
							var xupdate1 = this.shard[6];
							var yupdate1 = this.shard[7];
							var timing1 = this.shard[8];
	
							if (this.scene == 1) {
								this.update({delay: 0, time: timeupdate1, x: xupdate1, y: yupdate1, transition: timing(timing1)});
							}
						});

				MALTween[div].shard = shards[i];
			}
		}
					
		// scene one
		MALTween.add('MALAdBlackBackground', {delay: 0, time: 0.4170, opacity: 0, transition: timing('linear')});
		MALTween.add('MALAdArtist1', {delay: 0, time: 4.5045, x: 22, y: -106, transition: timing('linear')});

		MALTween.add('MALAdAlbum1', {delay: 3.8371, time: 1.3763, x: 21, y: 54, transition: timing('ease-out')})
				.addEvent('complete', function () {
					if (this.scene == 1) {
						this.update({delay: 1.1678, time: 0.7924, x: 45, y: 124, transition: timing('ease-in')});
					}
				});		
		MALTween.add('MALAdConstant', {delay: 4.7964, time: 4.8381, x: 44, y: 119, transition: timing('linear')});
				
		MALTween.add('MALAdShardContainer', {delay: 3.6703, time: 1.4180, y: -176, transition: timing('ease-in-out')});
		MALTween.add('MALAdBlackSolid', {delay: 6.7150, time: 0.6256, opacity: 0, transition: timing('0.43, 0.07, 0.91, 1')});
		MALTween.add('MALAdEndcardText', {delay: 6.3813, time: 1.4180, y: -49, transition: timing('0, 0, 0.38, 1')});
				
		MALTween.add('MALAdCTA', {delay: 8.4250, time: 0.7507, x: 108, transition: timing('ease-out')});
		MALTween.add('MALAdText', {delay: 9.1758, time: 0.7507, opacity: 1, transition: timing('linear')})
				.addEvent('complete', function () {
					MALAd.mouseOver = true;		
				});

		MALTween.start();
	};

	MALAd.clickThrough = function () {
		//alert('iTunes');
	};
	
	MALAd.checkDOM();

})(window, MALTween, MALAd);